import{_ as e,c as l,o as r,ag as a}from"./chunks/framework.Dfa2T_mi.js";const x=JSON.parse('{"title":"合伙人计划会员手册","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"membership/member.md","filePath":"membership/member.md","lastUpdated":1756695886000}'),n={name:"membership/member.md"};function s(o,t,i,d,g,h){return r(),l("div",null,t[0]||(t[0]=[a('<h1 id="合伙人计划会员手册" tabindex="-1">合伙人计划会员手册 <a class="header-anchor" href="#合伙人计划会员手册" aria-label="Permalink to &quot;合伙人计划会员手册&quot;">​</a></h1><p><strong>手册宗旨：</strong> 本手册旨在阐明“合伙人计划”会员制度的初衷、服务内容、特权及规则，旨在与各位优秀的校园伙伴建立长期、稳定、互惠共赢的合作关系，共同为毕业生提供优质的技术服务。</p><h2 id="一、-我们的初衷" tabindex="-1">一、 我们的初衷 <a class="header-anchor" href="#一、-我们的初衷" aria-label="Permalink to &quot;一、 我们的初衷&quot;">​</a></h2><p>我们深知，毕业设计是每一位同学大学最后的关卡。我们的使命并非简单的“赚钱”，而是提供<strong>专业的技术支持与项目辅导</strong>，帮助学弟学妹们克服技术难题，顺利完成学业。我们坚信，通过提供有价值的服务，可以获得合理的回报。我们希望聚集一群有责任心、有创业精神、愿意用技术服务他人的伙伴，一起打造一个值得信赖的校园技术服务平台。</p><h2 id="二、-会员与非会员权益对比" tabindex="-1">二、 会员与非会员权益对比 <a class="header-anchor" href="#二、-会员与非会员权益对比" aria-label="Permalink to &quot;二、 会员与非会员权益对比&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">权益项目</th><th style="text-align:left;"><strong>会员 (荣誉码友)</strong></th><th style="text-align:left;"><strong>非会员 (普通伙伴)</strong></th></tr></thead><tbody><tr><td style="text-align:left;"><strong>会员费用</strong></td><td style="text-align:left;">299元/年</td><td style="text-align:left;">免费</td></tr><tr><td style="text-align:left;"><strong>定制系统拿货价</strong></td><td style="text-align:left;"><strong>580元/套</strong></td><td style="text-align:left;">680元/套</td></tr><tr><td style="text-align:left;"><strong>发展下级代理</strong></td><td style="text-align:left;"><strong>允许</strong>，并可享受推荐奖励与管理津贴</td><td style="text-align:left;">不允许</td></tr><tr><td style="text-align:left;"><strong>技术支持</strong></td><td style="text-align:left;"><strong>优先响应</strong> (24小时内)，复杂问题协助解决</td><td style="text-align:left;">普通响应 (48小时内)，基础问题解答</td></tr><tr><td style="text-align:left;"><strong>营销素材</strong></td><td style="text-align:left;">获得全套最新话术、海报、产品目录</td><td style="text-align:left;">获得基础版营销素材</td></tr><tr><td style="text-align:left;"><strong>内部活动</strong></td><td style="text-align:left;">参与新品内测、特价促销活动</td><td style="text-align:left;">不享受</td></tr><tr><td style="text-align:left;"><strong>结算周期</strong></td><td style="text-align:left;">灵活结算</td><td style="text-align:left;">月底结算</td></tr></tbody></table><h2 id="三、-会员核心特权详解" tabindex="-1">三、 会员核心特权详解 <a class="header-anchor" href="#三、-会员核心特权详解" aria-label="Permalink to &quot;三、 会员核心特权详解&quot;">​</a></h2><ol><li><strong>底价特权：</strong> 会员享受定制系统最低580元的底价，这意味着每销售一单定制系统，你将拥有至少200元（单价780为例）以上的毛利空间，利润完全由你掌控。</li><li><strong>团队发展特权：</strong><ul><li>你可以招募和管理你自己的二级代理团队。</li><li>他们支付的会员费，你将直接获得<strong>100元</strong> 的推荐奖励。</li><li>你的二级代理每售出一套定制系统，你将额外获得<strong>20元</strong>的管理津贴。</li><li>这意味着即使你自己不卖货，也可以通过管理团队获得持续收入。</li></ul></li><li><strong>专属支持特权：</strong> 我们将为会员建立专属技术支持群，由技术总监直接入驻，提供最快、最专业的售前售后答疑。</li></ol><h2 id="四、-如何成为会员" tabindex="-1">四、 如何成为会员？ <a class="header-anchor" href="#四、-如何成为会员" aria-label="Permalink to &quot;四、 如何成为会员？&quot;">​</a></h2><ol><li>向你的推荐人（或直接联系项目负责人）表达意向。</li><li>支付299元年度会员费用。</li><li>将被邀请加入“合伙人”核心群，即刻享受所有特权。</li></ol><h2 id="五、-行为准则" tabindex="-1">五、 行为准则 <a class="header-anchor" href="#五、-行为准则" aria-label="Permalink to &quot;五、 行为准则&quot;">​</a></h2><ol><li><strong>严禁恶意竞争：</strong> 不得恶意压价、诋毁同伴，扰乱市场秩序。</li><li><strong>保密原则：</strong> 不得对外泄露会员拿货价、产品目录等核心商业信息。</li><li><strong>专业服务：</strong> 在与客户交流时，应秉持“技术支持”与“项目辅导”的理念，保持专业、耐心的服务态度。</li></ol><p>我们期待你的加入，让我们一起成长，共享收益！</p>',13)]))}const m=e(n,[["render",s]]);export{x as __pageData,m as default};
