import{_ as e,c as l,o,ag as i}from"./chunks/framework.Dfa2T_mi.js";const m=JSON.parse('{"title":"【实战】学生在线留言板软件开发需求文档","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"project-coach/message-board-demand.md","filePath":"project-coach/message-board-demand.md","lastUpdated":1746253746000}'),r={name:"project-coach/message-board-demand.md"};function t(n,a,s,d,h,c){return o(),l("div",null,a[0]||(a[0]=[i('<h1 id="【实战】学生在线留言板软件开发需求文档" tabindex="-1">【实战】学生在线留言板软件开发需求文档 <a class="header-anchor" href="#【实战】学生在线留言板软件开发需求文档" aria-label="Permalink to &quot;【实战】学生在线留言板软件开发需求文档&quot;">​</a></h1><h2 id="一、项目背景" tabindex="-1">一、项目背景 <a class="header-anchor" href="#一、项目背景" aria-label="Permalink to &quot;一、项目背景&quot;">​</a></h2><p>为满足学生日常交流与信息分享需求，开发一款简单易用的留言板软件，用户可通过注册登录后，在平台上发布留言、评论交流，实现便捷的信息交互。</p><h2 id="二、功能需求" tabindex="-1">二、功能需求 <a class="header-anchor" href="#二、功能需求" aria-label="Permalink to &quot;二、功能需求&quot;">​</a></h2><h3 id="一-用户模块" tabindex="-1">（一）用户模块 <a class="header-anchor" href="#一-用户模块" aria-label="Permalink to &quot;（一）用户模块&quot;">​</a></h3><ol><li><strong>注册功能</strong>：用户输入唯一的用户名、密码（密码需满足一定复杂度，至少包含字母、数字，长度6 - 16位）、邮箱（格式需符合邮箱规范）进行注册。系统验证信息无误后，将用户信息存储至数据库，并向注册邮箱发送验证邮件，用户点击邮件链接完成注册激活。</li><li><strong>登录功能</strong>：用户输入注册时的用户名和密码进行登录验证。若验证通过，系统生成登录凭证（如JWT令牌），用于后续用户操作的身份识别；若验证失败，提示用户“用户名或密码错误”。</li></ol><h3 id="二-留言模块" tabindex="-1">（二）留言模块 <a class="header-anchor" href="#二-留言模块" aria-label="Permalink to &quot;（二）留言模块&quot;">​</a></h3><ol><li><strong>发布留言</strong>：登录用户进入留言板首页，点击“发布留言”按钮，在弹出的编辑框中输入留言内容（内容长度限制在1 - 500字），点击“提交”按钮，将留言信息（包含用户ID、留言内容、发布时间）保存至数据库，并在留言板首页展示最新发布的留言。</li><li><strong>留言展示</strong>：留言板首页按时间倒序展示所有留言，每条留言显示发布用户的用户名、留言内容、发布时间。</li></ol><h3 id="三-评论回复模块" tabindex="-1">（三）评论回复模块 <a class="header-anchor" href="#三-评论回复模块" aria-label="Permalink to &quot;（三）评论回复模块&quot;">​</a></h3><ol><li><strong>评论留言</strong>：任何登录用户在某条留言下方点击“评论”按钮，在弹出的输入框中输入评论内容（长度限制1 - 200字），点击“提交”，评论信息（包含评论用户ID、被评论留言ID、评论内容、评论时间）存储至数据库，并展示在对应留言下方。</li><li><strong>回复评论</strong>：对于已发布的评论，其他登录用户可点击该评论下方的“回复”按钮，输入回复内容（长度限制1 - 200字），提交后回复信息（包含回复用户ID、被回复评论ID、回复内容、回复时间）存入数据库，并展示在对应评论下方。同时，支持发布留言的用户对所有评论进行回复。</li><li><strong>删除评论</strong>：评论发布者可在自己发布的评论旁边点击“删除”按钮，系统验证该评论的发布者与当前登录用户一致后，从数据库中删除该评论及其所有相关回复，并在页面实时更新展示。</li><li><strong>删除评论及关联回复</strong>：将该评论及其所有相关回复（即被回复评论 ID 为当前删除评论 ID 的所有回复信息）从数据库中删除，并在页面实时更新展示，不再显示该评论及其相关回复内容。</li><li><strong>删除留言及所有评论</strong>：将该留言从数据库中删除，同时删除该留言下的所有评论（即被评论留言 ID 为当前删除留言 ID 的所有评论信息）及其相关回复（即被回复评论 ID 对应上述被删除评论 ID 的所有回复信息），并在页面上移除该留言及其所有评论、回复内容，保持留言板展示内容的一致性和完整性。</li></ol><h2 id="三、非功能需求" tabindex="-1">三、非功能需求 <a class="header-anchor" href="#三、非功能需求" aria-label="Permalink to &quot;三、非功能需求&quot;">​</a></h2><h3 id="一-性能需求" tabindex="-1">（一）性能需求 <a class="header-anchor" href="#一-性能需求" aria-label="Permalink to &quot;（一）性能需求&quot;">​</a></h3><ol><li>系统应保证在100人同时在线使用时，页面加载时间不超过3秒。</li><li>留言、评论、回复等操作的响应时间不超过1秒。</li></ol><h3 id="二-安全性需求" tabindex="-1">（二）安全性需求 <a class="header-anchor" href="#二-安全性需求" aria-label="Permalink to &quot;（二）安全性需求&quot;">​</a></h3><ol><li>用户密码在数据库中需采用加密存储（如使用BCrypt算法）。</li><li>防止SQL注入、XSS攻击等常见安全漏洞，对用户输入内容进行严格的过滤和验证。</li></ol><h3 id="三-兼容性需求" tabindex="-1">（三）兼容性需求 <a class="header-anchor" href="#三-兼容性需求" aria-label="Permalink to &quot;（三）兼容性需求&quot;">​</a></h3><p>支持主流浏览器（如Chrome、Firefox、Edge），以及Windows、MacOS操作系统。</p><h2 id="四、技术框架" tabindex="-1">四、技术框架 <a class="header-anchor" href="#四、技术框架" aria-label="Permalink to &quot;四、技术框架&quot;">​</a></h2><ul><li>运行系统：windows/Mac</li><li>编程语言：Python/java/Node</li><li>系统架构：B/S</li><li>后端框框：SpringBoot/Flask/Django/Express</li><li>前端框架：Vue+HTML+JavaScript+Css</li><li>前后端分离：否</li><li>数据库：MySQL</li><li>数据库表数量：5</li><li>运行环境： <ul><li>JDK8+MySQL5.7+Maven3.6+IntelliJ IDEA(java)</li><li>Python3+MySQL5.7+Pip3+PyCharm(python)</li><li>Node14+MySQL5.7+NPM+VScode(node)</li></ul></li></ul>',19)]))}const g=e(r,[["render",t]]);export{m as __pageData,g as default};
